import React, { Component } from 'react';
import Piece from '../components/Piece'
import axios from 'axios';

export default class Index extends Component {
    static async getInitialProps() {
        let response;
        try {
            // environment variables should be available on server side. Should be problematic when this page is navigated to using a link
            response = await axios.get((this.props.crud_api_uri) + "/pieces");
        }
        catch (error) {
            console.log("error fetching /pieces");
        }
        return {
            pieces: response.data,
            crud_api_uri: this.props.crud_api_uri
        }
    }
    render() {
        const pieces = this.props.pieces.map(piece =>
            <Piece
                key={piece._id}
                title={piece.title}
                imageSrc={this.props.crud_api_uri + piece.image.url}
                description={piece.description}
            />
        );

        return <div className="container mt-5">
            <div className="row text-center">
                {pieces}
            </div>
        </div>
    }
}
