import React, { Component } from 'react';
import axios from 'axios';

export default class Index extends Component {
    static async getInitialProps() {
        try {
            let pieces = await axios.get(process.env.CRUD_API_URI + "/pieces");
            return {
                pieces: pieces
            }
        }
        catch (error) {
            console.log("error fetching /pieces");
        }
    }
    render() {
        return <div>
            <h1>The index page!</h1>
            <p>{this.props.pieces.map(piece => {
                piece.image
            })}</p>
        </div>
    }
}
