import React, { Component } from 'react';
import Piece from '../components/Piece';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import axios from 'axios';

export default class Index extends Component {
    static async getInitialProps() {
        let response;
        try {
            response = await axios.get(process.env.CRUD_API_URI + "/pieces");
        }
        catch (error) {
            console.log("error fetching /pieces");
            console.log(error);
        }
        return {
            pieces: response.data
        }
    }
    constructor(props) {
        super(props);
        this.state = {
            show: false,
            piece: null
        }
        this.handleClose = this.handleClose.bind(this);
        this.handleShow = this.handleShow.bind(this);
    }

    handleClose() {
        this.setState({
            show: false,
            piece: null
        });
    }

    handleShow(pieceData) {
        this.setState({
            show: true,
            piece: pieceData
        });
    }

    render() {
        const pieces = this.props.pieces.map(piece =>
            <Piece
                key={piece._id}
                title={piece.title}
                imageSrc={process.env.IMAGE_BASE_URI + piece.image.url}
                description={piece.description}
                handleShow={this.handleShow}
            />
        );

        return <Container>
            <Row className="text-center">
                {pieces}
            </Row>

            <Modal show={this.state.show} onHide={this.handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>{this.state.piece != null ? this.state.piece.title : null}</Modal.Title>
                </Modal.Header>
                <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={this.handleClose}>
                        Close
          </Button>
                    <Button variant="primary" onClick={this.handleClose}>
                        Save Changes
          </Button>
                </Modal.Footer>
            </Modal>
        </Container>
    }
}
